
// Audio handling for alarms

let alarmAudio: HTMLAudioElement | null = null;
const DEFAULT_ALARM_SOUND = "/sounds/alarm.mp3"; // Default fallback sound

// Preload audio element to avoid playback issues on mobile
export const preloadAlarmSound = (soundPath?: string): HTMLAudioElement => {
  const soundUrl = soundPath || DEFAULT_ALARM_SOUND;
  const audio = new Audio(soundUrl);
  
  // Enable auto-play on mobile by adding user interaction event listeners
  document.addEventListener('touchstart', () => {
    // Create and play a silent audio to enable audio context
    const silentAudio = new Audio("data:audio/mp3;base64,");
    silentAudio.play().then(() => {
      silentAudio.pause();
      silentAudio.remove();
    }).catch(e => console.log("Silent audio play failed:", e));
  }, { once: true });
  
  // Improve mobile and web compatibility
  audio.setAttribute('playsinline', ''); 
  audio.setAttribute('webkit-playsinline', '');
  
  return audio;
};

// Start playing alarm sound
export const startAlarmSound = (soundPath?: string): void => {
  if (alarmAudio) {
    alarmAudio.pause();
    alarmAudio = null;
  }
  
  alarmAudio = preloadAlarmSound(soundPath);
  alarmAudio.loop = true;
  alarmAudio.volume = 1.0; // Full volume for mobile
  
  // Ensure audio plays even if the app is in background
  try {
    // Play the alarm sound with retry mechanism
    const playWithRetry = (retries = 3) => {
      alarmAudio!.play()
        .then(() => {
          console.log("Alarm sound playing successfully");
        })
        .catch(error => {
          console.error("Error playing alarm sound:", error);
          
          // Try again with user interaction if available
          if (retries > 0) {
            console.log(`Retrying playback, ${retries} attempts left`);
            window.setTimeout(() => playWithRetry(retries - 1), 1000);
          }
        });
    };
    
    playWithRetry();
  } catch (e) {
    console.error("Critical error triggering alarm sound:", e);
  }
};

// Stop alarm sound
export const stopAlarmSound = (): void => {
  if (alarmAudio) {
    alarmAudio.pause();
    alarmAudio.currentTime = 0;
    alarmAudio = null;
  }
};
